    return (
        <div>
            <h1>Productos Destacados</h1>
            <Row>
                <div className="productos">
                    {data.products.map((product) => (
                        <Col sm={6} md={4} lg={3} className="mn-3">
                            <Product product={product}></Product>
                        </Col>
                    ))}
                </div>
            </Row>
        </div>
    );
}

export default HomeScreen;
